import React from 'react'
import styled from 'styled-components'
import { FaGithub } from 'react-icons/fa'
import { useLocation } from 'react-router-dom'
import backgroundWave from '../assets/images/background-wave.png'
import routes from '../constants/routes.json'

const Footer = () => {
  const location = useLocation()

  return (
    <FooterWrapper>
      Copyright Â© 2023 Thomas Thabethe
      <GithubLink
        href="https://github.com/Thomas-boop"
        target="_blank"
        rel="noopener"
      >
        <FaGithub />
      </GithubLink>
      {location.pathname !== routes.PRODUCTS && (
        <BackgroundWave src={backgroundWave} alt="background image" />
      )}
    </FooterWrapper>
  )
}

const FooterWrapper = styled.footer`
  position: absolute;
  bottom: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 1rem 0;
  font-size: 2rem;
`

const GithubLink = styled.a`
  display: flex;
  margin-left: 1rem;
  color: ${({ theme }) => theme.colors.dark};
  font-size: 2rem;
  transition: transform 0.15s ease-in-out;

  &:hover {
    transform: scale(1.2);
  }
`
const BackgroundWave = styled.img`
  position: absolute;
  bottom: 0;
  left: 0;
  z-index: -100;
`

export default Footer
